  return (
    <div className="App">
      <Mensaje color="red" mensaje="Hola" />
      <Mensaje color="blue" mensaje="ADIOS" />
      <Mensaje color="yellow" mensaje="que tal" />
      <Descripcion />

    </div>
  );
};

